INSERT INTO `rvo_posts` VALUES("71", "1", "2020-12-03 06:29:45", "2020-12-03 05:29:45", "<!-- wp:heading {\"align\":\"left\"} -->\n<h2 class=\"has-text-align-left\">Qui sommes nous ? </h2>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<br><br>\n<!-- /wp:html -->\n\n<!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:media-text {\"mediaId\":84,\"mediaType\":\"image\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost:8888/wordpress:renov_objet/wp-content/uploads/2020/12/IMG_2942-820x1024.jpg\" alt=\"Lampe\" class=\"wp-image-84\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"placeholder\":\"Contenu\",\"style\":{\"typography\":{\"fontSize\":17}}} -->\n<p style=\"font-size:17px\">Nous sommes un pre et son fils bricoleur, qui ont eu comme ide de prendre un disque de frein usag et un serre joint qui trainais au fond de l\'atelier afin d\'en faire une lampe. Nous avons tellement eu de plaisir a travailler tout les deux que nous nous retrouvions le weekend d\'aprs pour crer une nouvelle lampe.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:html -->\n<br><br><br>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:heading -->\n<h2>Des objets unique ...</h2>\n<!-- /wp:heading -->\n\n<!-- wp:group {\"backgroundColor\":\"white\",\"textColor\":\"black\"} -->\n<div class=\"wp-block-group has-black-color has-white-background-color has-text-color has-background\"><div clas