INSERT INTO `rvo_posts` VALUES("194", "1", "2020-12-03 12:31:46", "2020-12-03 11:31:46", "<!-- wp:heading {\"align\":\"left\"} -->\n<h2 class=\"has-text-align-left\">Qui sommes nous ? </h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Nous sommes un père et son fils bricoleur, qui ont eu un jour comme idée de prendre un disque de frein usagé et un serre joint qui trainais au fond de l\'atelier afin de faire une lampe. Nous avons tellement eu de plaisir a travailler tout les deux que nous nous retrouvions le weekend d\'après pour créer une nouvelle lampe et voilà deux petites lampes.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"align\":\"center\",\"id\":84,\"width\":410,\"height\":512,\"sizeSlug\":\"large\",\"className\":\"is-style-rounded\"} -->\n<div class=\"wp-block-image is-style-rounded\"><figure class=\"aligncenter size-large is-resized\"><img src=\"http://localhost:8888/wordpress:renov_objet/wp-content/uploads/2020/12/IMG_2942-820x1024.jpg\" alt=\"\" class=\"wp-image-84\" width=\"410\" height=\"512\"/></figure></div>\n<!-- /wp:image --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:heading -->\n<h2>Des objets unique ...</h2>\n<!-- /wp:heading -->\n\n<!-- wp:group {\"backgroundColor\":\"white\",\"textColor\":\"black\"} -->\n<div class=\"wp-block-group has-black-color has-white-background-color has-text-color has-background\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Nos objets sont unique car fait à la main. Nous partons, pour chaque objet d\'une feuille blanche et nous amenons petit à petit nos idées. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">\"Papa j\'ai une idée mais pour la réalisation tu risque de me détester ...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">-Dis toujours on verra </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">[...]</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">- C\'est vrais que cela rendrait vraiment bien .. Bon on va essayer de le faire mais je te garantis rien !\" </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gt3pg-pro/grid {\"_uid\":\"933c6f19-b64d-462f-97fc-ace5f505182b\",\"_blockName\":\"grid\",\"ids\":\"[{\\u0022id\\u0022:117,\\u0022videoLink\\u0022:{\\u0022url\\u0022:\\u0022\\u0022,\\u0022id\\u0022:0,\\u0022video_type\\u0022:\\u0022hosted\\u0022,\\u0022hosted_id\\u0022:0,\\u0022hosted_url\\u0022:\\u0022\\u0022,\\u0022youtube_url\\u0022:\\u0022\\u0022,\\u0022vimeo_url\\u0022:\\u0022\\u0022},\\u0022externalLink\\u0022:{\\u0022url\\u0022:\\u0022\\u0022,\\u0022target\\u0022:\\u0022\\u0022}},{\\u0022id\\u0022:115,\\u0022videoLink\\u0022:{\\u0022url\\u0022:\\u0022\\u0022,\\u0022id\\u0022:0,\\u0022video_type\\u0022:\\u0022hosted\\u0022,\\u0022hosted_id\\u0022:0,\\u0022hosted_url\\u0022:\\u0022\\u0022,\\u0022youtube_url\\u0022:\\u0022\\u0022,\\u0022vimeo_url\\u0022:\\u0022\\u0022},\\u0022externalLink\\u0022:{\\u0022url\\u0022:\\u0022\\u0022,\\u0022target\\u0022:\\u0022\\u0022}},{\\u0022id\\u0022:113,\\u0022videoLink\\u0022:{\\u0022url\\u0022:\\u0022\\u0022,\\u0022id\\u0022:0,\\u0022video_type\\u0022:\\u0022hosted\\u0022,\\u0022hosted_id\\u0022:0,\\u0022hosted_url\\u0022:\\u0022\\u0022,\\u0022youtube_u