INSERT INTO `rvo_posts` VALUES("209", "1", "2020-12-03 13:03:52", "2020-12-03 12:03:52", "<!-- wp:media-text {\"mediaId\":163,\"mediaType\":\"image\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost:8888/wordpress:renov_objet/wp-content/uploads/2020/12/IMG_4907-768x1024.jpg\" alt=\"Ancien fer à souder transformé en lampe\" class=\"wp-image-163\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"placeholder\":\"Contenu…\",\"style\":{\"typography\":{\"fontSize\":18}}} -->\n<p style=\"font-size:18px\">Anciennement, on m\'utilisait pour souder du métal, c\'est avec ce même procédé de soudure que je me suis transformé en lampe. Je repose sur mon socle qui appartenait à une vieille table en chêne.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:html -->\n<br><br><br>\n<!-- /wp:html -->\n\n<!-- wp:gt3pg-pro/grid {\"_uid\":\"cc6dcf98-47f5-496e-9bbf-1d3a7f12db7c\",\"_blockName\":\"grid\",\"ids\":\"[{\\u0022id\\u0022:163,\\u0022videoLink\\u0022:{\\u0022url\\u0022:\\u0022\\u0022,\\u0022id\\u0022:0,\\u0022video_type\\u0022:\\u0022hosted\\u0022,\\u0022hosted_id\\u0022:0,\\u0022hosted_url\\u0022:\\u0022\\u0022,\\u0022youtube_url\\u0022:\\u0022\\u0022,\\u0022vimeo_url\\u0022:\\u0022\\u0022},\\u0022externalLink\\u0022:{\\u0022url\\u0022:\\u0022\\u0022,\\u0022target\\u0022:\\u0022\\u0022}},{\\u0022id\\u0022:164,\\u0022videoLink\\u0022:{\\u0022url\\u0022:\\u0022\\u0022,\\u0022id\\u0022:0,\\u0022video_type\\u0022:\\u0022hosted\\u0022,\\u0022hosted_id\\u0022:0,\\u0022hosted_url\\u0022:\\u0022\\u0022,\\u0022youtube_url\\u0022:\\u0022\\u0022,\\u0022vimeo_url\\u0022:\\u0022\\u0022},\\u0022externalLink\\u0022:{\\u0022url\\u0022:\\u0022\\u0022,\\u0022target\\u0022:\\u0022\\u0022}},{\\u0022id\\u0022:162,\\u0022videoLink\\u0022:{\\u0022url\\u0022:\\u0022\\u0022,\\u0022id\\u0022:0,\\u0022video_type\\u0022:\\u0022hosted\\u0022,\\u0022hosted_id\\u00